import React, { useEffect, useState } from "react"
import { Streamlit, withStreamlitConnection, ComponentProps } from "streamlit-component-lib"

interface Player {
  id: string
  bbox: [number, number, number, number]
}

interface Props extends ComponentProps {
  args: {
    image: string
    players: Player[]
    assignments: Record<string, { team: "1" | "2"; removed: boolean }>
    colors: { "1": string; "2": string; removed: string }
  }
}

const MyComponent = ({ args }: Props) => {
  const [assignments, setAssignments] = useState(args.assignments || {})

  // ðŸ§ª Logging beim ersten Render
  useEffect(() => {
    console.log("ðŸ“¸ Bilddaten (Base64 LÃ¤nge):", args.image?.length)
    console.log("ðŸ‘¥ Spieler:", args.players)
    console.log("ðŸŽ¨ Farben:", args.colors)
  }, [])

  const onBoxClick = (id: string, shiftKey: boolean) => {
    const current = assignments[id] || { team: "1", removed: false }

    const updated = {
      ...assignments,
      [id]: shiftKey
        ? { ...current, removed: !current.removed }
        : {
            team: current.team === "1" ? "2" : "1",
            removed: false,
          },
    }

    setAssignments(updated as Record<string, { team: "1" | "2"; removed: boolean }>)
    Streamlit.setComponentValue(updated)
  }

  return (
    <div style={{ position: "relative", display: "inline-block", border: "2px dashed green" }}>
      <img
        src={`data:image/png;base64,${args.image}`}
        alt="Frame"
        style={{
          display: "block",
          width: "100%",
          height: "auto",
          position: "relative",
          zIndex: 0,
          border: "1px solid #ccc",
        }}
      />
      {args.players.map((player) => {
        const a = assignments[player.id] || { team: "1", removed: false }
        const color = a.removed ? args.colors.removed : args.colors[a.team]
        const [x1, y1, x2, y2] = player.bbox
        return (
          <div
            key={player.id}
            onClick={(e) => onBoxClick(player.id, e.shiftKey)}
            style={{
              position: "absolute",
              top: y1,
              left: x1,
              width: x2 - x1,
              height: y2 - y1,
              border: `2px solid ${color}`,
              boxSizing: "border-box",
              cursor: "pointer",
              zIndex: 1,
            }}
          />
        )
      })}
    </div>
  )
}


export default withStreamlitConnection(MyComponent)
